SELECT oodn.shop_sid, oodn.shop_name, oon.pay_time, oodn.supply_info_sid, oodn.brand_name, oodn.brand_sid, oon.payment_type_sid, oodn.cashier_number, oodn.sale_sum, (oodn.sale_sum * oodn.sale_price) sale_all_price, ooi.device_en, ooi.bank_no, ooi.ref_no, ooi.terminal_no, oodn.opt_user_name
FROM oms_order_new oon, oms_order_info ooi, oms_order_detail_new oodn
oon.order_no = ooi.order_no AND oon.order_no = oodn.order_no and oon.order_status >=2 and oon.payment_type_sid = 44
and oodn.supply_info_sid = #{supplySid}
and oon.payment_type_sid = #{paymentTypeSid}
and oodn.shop_sid = #{shopSid}
and ooi.terminal_no = #{terminalNo}
and oodn.cashier_number = #{cashierNumber}
and oodn.opt_user_name = #{guideNo}
and oon.pay_time >= #{startTime}
and oon.pay_time <= #{endTime}
LIMIT #{start},#{limit}

SELECT
sum(oodn.sale_sum * oodn.sale_price) sale_all_price, ooi.terminal_no, ooi.cashier_no, oodn.opt_user_name
FROM oms_order_new oon, oms_order_info ooi, oms_order_detail_new oodn
oon.order_no = ooi.order_no AND oon.order_no = oodn.order_no and oon.order_status >=2 and oon.payment_type_sid = 44
and ooi.terminal_no = #{terminalNo}
and oodn.opt_user_name = #{guideNo}
and oon.pay_time >= #{startTime}
and oon.pay_time <= #{endTime}
group by oodn.opt_user_name LIMIT #{start},#{limit}

SELECT
sum(oodn.sale_sum * oodn.sale_price) sale_all_price, ooi.terminal_no, ooi.cashier_no, oodn.opt_user_name
FROM oms_order_new oon, oms_order_info ooi, oms_order_detail_new oodn
oon.order_no = ooi.order_no AND oon.order_no = oodn.order_no and oon.order_status >=2 and oon.payment_type_sid = 44
and ooi.terminal_no = #{terminalNo}
and oodn.opt_user_name = #{guideNo}
and oon.pay_time >= #{startTime}
and oon.pay_time <= #{endTime}
group by oodn.opt_user_name

select count(*) from ( SELECT sum(oodn.sale_sum * oodn.sale_price) sale_all_price, ooi.terminal_no, oodn.opt_user_name
FROM oms_order_new oon, oms_order_info ooi, oms_order_detail_new oodn
oon.order_no = ooi.order_no AND oon.order_no = oodn.order_no and oon.order_status >=2 and oon.payment_type_sid = 44
and ooi.terminal_no = #{terminalNo}
and oodn.opt_user_name = #{guideNo}
and oon.pay_time >= #{startTime}
and oon.pay_time <= #{endTime}
group by oodn.opt_user_name ) as v

