mapper.select:
select * from (SELECT oodn.shop_sid shop_sid, oon.pay_time pay_time, oodn.supply_info_sid supply_info_sid, oodn.brand_name brand_name, oodn.brand_sid brand_sid, oon.payment_type_sid payment_type_sid, oodn.cashier_number cashier_number, oodn.sale_sum sale_sum, (oodn.sale_sum * oodn.sale_price) sale_all_price, ooi.device_en device_en, ooi.bank_no bank_no, ooi.ref_no ref_no, ooi.terminal_no terminal_no, oodn.opt_user_name opt_user_name
FROM oms_order_new oon, oms_order_info ooi, oms_order_detail_new oodn
oon.order_no = ooi.order_no AND oon.order_no = oodn.order_no and oon.order_status >=2 and oon.payment_type_sid = 44
and oodn.supply_info_sid = #{supplySid}
and oon.payment_type_sid = #{paymentTypeSid}
and oodn.shop_sid = #{shopSid}
and ooi.terminal_no = #{terminalNo}
and oodn.cashier_number = #{cashierNumber}
and oodn.opt_user_name = #{guideNo}
and oon.pay_time >= #{startTime}
and oon.pay_time <= #{endTime}
UNION ALL
SELECT ordn.shop_sid shop_sid, orn.cash_return_time pay_time, ordn.supply_info_sid supply_info_sid, ordn.brand_name brand_name, ordn.brand_sid brand_sid, orn.payment_type_sid payment_type_sid, ordn.cashier_number cashier_number, ordn.refund_sum sale_sum, ( (-1)*ordn.refund_price * ordn.refund_sum ) sale_all_price, ori.device_en device_en, ori.bank_no bank_no, ori.ref_no ref_no, ori.terminal_no terminal_no, ordn.opt_user_name opt_user_name
FROM oms_refund_new orn, oms_refund_info ori, oms_refund_detail_new ordn
orn.refund_no = ori.refund_no AND orn.refund_no = ordn.refund_no AND orn.refund_status >= 3 AND orn.payment_type_sid = 44
AND ordn.supply_info_sid = #{supplySid}
AND ordn.shop_sid = #{shopSid}
AND ori.terminal_no = #{terminalNo}
AND ordn.cashier_number = #{cashierNumber}
AND ordn.opt_user_name = #{guideNo}
and orn.cash_return_time >= #{startTime}
and orn.cash_return_time <= #{endTime}
) as bb
order by pay_time desc LIMIT #{start},#{limit}

select *
from( SELECT sum(oodn.sale_sum * oodn.sale_price) sale_all_price, ooi.terminal_no terminal_no, ooi.cashier_no cashier_no, oodn.opt_user_name opt_user_name
FROM oms_order_new oon, oms_order_info ooi, oms_order_detail_new oodn
oon.order_no = ooi.order_no and oon.order_no = oodn.order_no and oon.order_status >=2 and oon.payment_type_sid = 44
and ooi.terminal_no = #{terminalNo}
and oodn.opt_user_name = #{guideNo}
and oon.pay_time >= #{startTime}
and oon.pay_time <= #{endTime}
group by ooi.terminal_no
UNION ALL
SELECT sum( (-1)*ordn.refund_price * ordn.refund_sum ) sale_all_price, ori.terminal_no terminal_no, ordn.cashier_number cashier_no, ordn.opt_user_name opt_user_name
FROM oms_refund_new orn, oms_refund_info ori, oms_refund_detail_new ordn
orn.refund_no = ori.refund_no AND orn.refund_no = ordn.refund_no AND orn.refund_status >= 3 AND orn.payment_type_sid = 44
AND ori.terminal_no = #{terminalNo}
AND ordn.opt_user_name = #{guideNo}
and orn.cash_return_time >= #{startTime}
and orn.cash_return_time <= #{endTime}
group by ori.terminal_no
) as bb
LIMIT #{start},#{limit}

